import React from "react";
import "./newChat.css";
import userImg from "./Vijay_YNM.jpg";
import VinuImg from "./Vinu.jpg";
import Tlogo from "./Vijay_logo.jpg";
import Jaya from "./jayaprakash.jpeg";
import SearchIcon from '@mui/icons-material/Search';
import SendIcon from '@mui/icons-material/Send';
import classNames from "classnames"; 
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { useEffect, useState } from 'react';
import firebase from 'firebase/compat/app';
import Button from 'react-bootstrap/Button';
import axios from "axios";
import AddIcon from '@mui/icons-material/Add';
import 'firebase/compat/firestore';
import { Scrollbars } from 'react-custom-scrollbars';
import { Link, Outlet, useNavigate, useParams,useLocation } from "react-router-dom";
function ChatingApp(){
  let Profileid=localStorage.getItem("proid",Profileid)

  var ChatViewid=Profileid;
  let tosend_userid='10';
  
  var Docu_ChatViewid;

  const[message,setmessage]=useState([])
  const [text, setText] = useState('');
  const [chatBlocklist,setChatblocklist]=useState([])
  const timestamp = firebase.firestore.Timestamp.now();
  const [chatblock, setChatblock] = useState([]);
  const [ticketChatList, setTicketChatList] = useState([]);
  const [sendTo_userid, setsendTo_userid] = useState(tosend_userid);
  useEffect(() => {
    axios.get(`http://localhost:5000/Chating_employeelist`)
      .then((res) => {
        const ticketChatList = res.data.data;
        setTicketChatList(ticketChatList);
  
        const updatedChatBlock = searchInput
          ? ticketChatList.filter(item =>
              item.firstname.toLowerCase().includes(searchInput.toLowerCase())
            )
          : ticketChatList;
        console.log("Updated chatBlock is",updatedChatBlock)
        setChatblock(updatedChatBlock);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [searchInput]); 
  
  useEffect(() => {
  if (ticketChatList.length > 0) {
      setChatblock(ticketChatList);
    }
  }, [ticketChatList]);
  const sendMessage = () => {
  const db = firebase.firestore();
  const collectionName = 'ChatApp';
 
  
  const data = {
    text,
    createdAt: timestamp,
    userid:sendTo_userid,
    profileid:Profileid
  
  };
  
  const subcollectionName = 'SubChat'; 
    if (text.trim() !== '') {
      db.collection(collectionName).doc(ticketid).set(data)
      .then(() => {
       db.collection(collectionName).doc(ticketid).collection(subcollectionName).add(data)
          .then(() => {
          
          })
          .catch(error => {
          
          });
      })
      .catch(error => {
        console.error("Error writing main document: ", error);
      });
      setText(''); 
  };
  }


const chatListItems = searchInput
  ? searchResults.map((item, index) => (
    <div className="chatItem" key={index}>
    <div className="imgbx">
      <img src={"http://localhost:5000/images/"+ item.id}  className="cover"  />
    </div>
    <div className="chatInfo">
      <p className="chatName" onClick={()=>chatblock_process(item.id,item.firstname)}>{item.firstname}</p>
    </div>
  </div>
  
    )): chatblock.map((item, index) => (
    <div className="chatItem" key={index}>
    <div className="imgbx">
      <img src={"http://localhost:5000/images/"+ item.id}  className="cover"  />
    </div>
    <div className="chatInfo">
      <p className="chatName" onClick={()=>chatblock_process(item.id,item.firstname)}>{item.firstname}</p>
      </div>
  </div>
    ));
const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      sendMessage();
    }
  };

useEffect(() => {
  const unsubscribe = firebase.firestore().collection('ChatApp').doc(Profileid).collection('SubChat')
      .orderBy('createdAt')
      .onSnapshot(snapshot => {
        const messages = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
        }));
        setmessage(messages);
      });
  
    return () => unsubscribe();
  }, []);


let Profilename=localStorage.getItem("profileName",Profilename);

const [ticketid, setTicketid] = useState(ChatViewid);
const [chatuser_name,setChatuser_name] = useState(Profilename);

const chatblock_process=(id,firstname)=>{
  Docu_ChatViewid=id
  ChatViewid=id;

  setChatuser_name(firstname)
  const duplicate_docid = ChatViewid.toString();

  setTicketid(duplicate_docid)
  const unsubscribe = firebase.firestore().collection('ChatApp').doc(duplicate_docid).collection('SubChat')
        .orderBy('createdAt')
        .onSnapshot(snapshot => {
        const messages = snapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data(),
          }));  
        setmessage(messages);
        });
    
      return () => unsubscribe();
    
    }
let prevDate = null;  
const [searchInput, setSearchInput] = useState('');
const [searchResults, setSearchResults] = useState([]);
const handleSearch = (query) => {

  setSearchInput(query);

  // Perform search logic (e.g., filter chatblock based on the query)
  const results = chatblock.filter((item) =>
    item.firstname.toLowerCase().includes(query.toLowerCase())
  );
  setSearchResults(results);
  console.log("Search input query is ",results)

};
  return(
    <div className="chatroom-container">
          <div className="left-side">
          <div className="header">
         
          <div className="backButton">
          <ArrowBackIcon className="backicon" />
          <div className="chat_heading">
            <p>Chat</p>
            </div>
        </div>
            <div className="userImage">
              {/* <img src={userImg} className='profile_chatheader'/> */}
            </div>

          </div>

      
          <div className="search-chat">
            <div>
              <input
      type="text"
      placeholder="Search or start new chat"
      value={searchInput}
      onChange={(e) => handleSearch(e.target.value)}
    />
              <SearchIcon className="Search_icon"/>
            </div>
          </div>

          

          <div className="chatList">
    
          {chatListItems}
        </div>

          </div>

          <div className="right-side">
          <div className="chatHeader">
          <div className="userImage">
            <img src={"http://localhost:5000/images/"+ ticketid}  className="cover" alt="Chat User" />
          </div>
          <div className="userInfo">
            <p className="chatName">{chatuser_name}</p>
            <p className="status">Online</p>
          </div>
        </div>
        <div className="chatMessages">


        <div className="chatMessages">
        {message.map((message, index) => {
  const messageDate = message.createdAt.toDate();
  const formattedDate = messageDate.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });
  const formattedTime = messageDate.toLocaleTimeString('en-US', {
    hour: 'numeric',
    minute: 'numeric',
  });


  const showDate = prevDate !== formattedDate;
  
  prevDate = formattedDate;
  

  return (
    <React.Fragment key={index}>
            {showDate && <div className="messageDate">{formattedDate}</div>}
      <div
        key={index}
        className={`message ${ message.profileid === Profileid ? 'outgoing ' : 'incoming'}`}
      >
        <p className="chat_msgstl">{message.text}</p>
        <span className="time">{formattedTime}</span>
      </div>
    </React.Fragment>
  );
})}

</div>
  

</div>

        
<div className="messageInput">
          <input type="text"  value={text} onKeyDown={handleKeyPress} onChange={e => setText(e.target.value)} placeholder="Type a message..." />
          <SendIcon className="sendIcon"  onClick={sendMessage} />
        </div>

 
 
          

        
  
          </div>
          
      </div>
  )
}
export default ChatingApp;
