import React from "react";
import "./newChat.css";
import userImg from "./Vijay_YNM.jpg";
import VinuImg from "./Vinu.jpg";
import Tlogo from "./Vijay_logo.jpg";
import Jaya from "./jayaprakash.jpeg";
import SearchIcon from '@mui/icons-material/Search';
import SendIcon from '@mui/icons-material/Send';
import classNames from "classnames"; 
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { useEffect, useState } from 'react';
import firebase from 'firebase/compat/app';
import Button from 'react-bootstrap/Button';
import axios from "axios";
import AddIcon from '@mui/icons-material/Add';
import 'firebase/compat/firestore';
import { Scrollbars } from 'react-custom-scrollbars';
import { Link, Outlet, useNavigate, useParams,useLocation } from "react-router-dom";
function ChatingApp(){
  let Profileid=localStorage.getItem("proid",Profileid)

  var ChatViewid=Profileid;
  let tosend_userid='10';
  
  var Docu_ChatViewid;

  const[message,setmessage]=useState([])
  const [text, setText] = useState('');
  const [chatBlocklist,setChatblocklist]=useState([])
  const timestamp = firebase.firestore.Timestamp.now();
  const [chatblock, setChatblock] = useState([]);
  const [ticketChatList, setTicketChatList] = useState([]);
  const [sendTo_userid, setsendTo_userid] = useState(null);

  useEffect(() => {
    setsendTo_userid(tosend_userid)
    axios.get(`http://localhost:5000/Chating_employeelist`)
      .then((res) => {
        const ticketChatList = res.data.data;
        setTicketChatList(ticketChatList);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []); 
  
  useEffect(() => {
  if (ticketChatList.length > 0) {
      setChatblock(ticketChatList);
    }
  }, [ticketChatList]);
  

  const sendMessage = () => {
    const db = firebase.firestore();
    const subcollectionName = 'SubChat';

    const data = {
      text,
      createdAt: timestamp,
      from_uid: Profileid,
      to_uid: sendTo_userid,
      sendermsg_id: Profileid,
    };

    if (text.trim() !== '') {
      // Add the message to the sender's subcollection
      db.collection('users')
        .doc(Profileid)
        .collection(subcollectionName)
        .add(data)
        .then(() => {
          // Also add the message to the receiver's subcollection
          db.collection('users')
            .doc(sendTo_userid)
            .collection(subcollectionName)
            .add(data)
            .then(() => {
              // Message sent successfully
              setText('');
            })
            .catch((error) => {
              console.error('Error sending message to receiver:', error);
            });
        })
        .catch((error) => {
          console.error('Error sending message to sender:', error);
        });
    }
  };


const chatListItems = searchInput
  ? searchResults.map((item, index) => (
    <div className="chatItem" key={index}>
    <div className="imgbx">
      <img src={"http://localhost:5000/images/"+ item.id}  className="cover"  />
    </div>
    <div className="chatInfo">
      <p className="chatName" onClick={()=>chatblock_process(item.id,item.firstname)}>{item.firstname}</p>
    </div>
  </div>
  
    )): chatblock.map((item, index) => (
    <div className="chatItem" key={index}>
    <div className="imgbx">
      <img src={"http://localhost:5000/images/"+ item.id}  className="cover"  />
    </div>
    <div className="chatInfo">
      <p className="chatName" onClick={()=>chatblock_process(item.id,item.firstname)}>{item.firstname}</p>
      </div>
  </div>
    ));
const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      sendMessage();
    }
  };

  useEffect(() => {
    const db = firebase.firestore();

    // Create references to sender's and receiver's message subcollections
    const senderMessagesRef = db.collection('users').doc(Profileid).collection('messages');
    const receiverMessagesRef = db.collection('users').doc(sendTo_userid).collection('messages');

    // Combine and listen to both collections
    const combinedRef = db.collectionGroup('messages');
    const unsubscribe = combinedRef
      .where('from_uid', 'in', [Profileid, sendTo_userid])
      .where('to_uid', 'in', [Profileid, sendTo_userid])
      .orderBy('createdAt')
      .onSnapshot((snapshot) => {
        const messages = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setMessage(messages);
      });

    return () => unsubscribe();
  }, [Profileid, sendTo_userid]);


let Profilename=localStorage.getItem("profileName",Profilename);

const [ticketid, setTicketid] = useState(ChatViewid);
const [chatuser_name,setChatuser_name] = useState(Profilename);
const [sendmsgId,setSendmsgId ] = useState(Profileid);

const chatblock_process=(id,firstname)=>{
  Docu_ChatViewid=id
  ChatViewid=id;
  setSendmsgId(id)
  setChatuser_name(firstname)
  setActiveUser(id);
  setsendTo_userid(id)
  const duplicate_docid = ChatViewid.toString();
  console.log("Duplicate Static docId",(duplicate_docid))
  console.log("sender_id printed",(sendTo_userid))

  setTicketid(duplicate_docid)
  const unsubscribe = firebase.firestore().collection('ChatApp').doc(duplicate_docid).collection('SubChat')
        .orderBy('createdAt')
        .onSnapshot(snapshot => {
        const messages = snapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data(),
          }));  
        setmessage(messages);
        });
    
      return () => unsubscribe();
    
