import React from "react";
import "./newChat.css";
import userImg from "./Vijay_YNM.jpg";
import VinuImg from "./Vinu.jpg";
import Tlogo from "./Vijay_logo.jpg";
import Jaya from "./jayaprakash.jpeg";
import SearchIcon from '@mui/icons-material/Search';
import SendIcon from '@mui/icons-material/Send';
import classNames from "classnames"; 
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { useEffect, useState } from 'react';
import firebase from 'firebase/compat/app';
import Button from 'react-bootstrap/Button';
import axios from "axios";
import AddIcon from '@mui/icons-material/Add';
import 'firebase/compat/firestore';
import { Scrollbars } from 'react-custom-scrollbars';
import { Link, Outlet, useNavigate, useParams,useLocation } from "react-router-dom";
import { v4 as uuidv4 } from 'uuid';
function ChatingApp(){
  let Profileid=localStorage.getItem("proid",Profileid)
  const generatedID = uuidv4();
  var ChatViewid=Profileid;
  // let tosend_userid='10';
  
  var Docu_ChatViewid;

  const[message,setmessage]=useState([])
  const [text, setText] = useState('');
  const [chatBlocklist,setChatblocklist]=useState([])
  const timestamp = firebase.firestore.Timestamp.now();
  const [chatblock, setChatblock] = useState([]);
  const [ticketChatList, setTicketChatList] = useState([]);
  const [sendTo_userid, setsendTo_userid] = useState('');
  useEffect(() => {
    // setsendTo_userid(tosend_userid)
    axios.get(`http://localhost:5000/Chating_employeelist`)
      .then((res) => {
        const ticketChatList = res.data.data;
        setTicketChatList(ticketChatList);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []); 
  
  useEffect(() => {
  if (ticketChatList.length > 0) {
      setChatblock(ticketChatList);
    }
  }, [ticketChatList]);



  const [existingConversations, setExistingConversations] = useState([]);
  const sendMessage = () => {
    const db = firebase.firestore();
    const collectionName = 'chats';
    const data = {
      text,
      createdAt: timestamp,
      from_uid: Profileid,
      to_uid: sendmsgId,
    };
  
    const subcollectionName = 'SubChat';
  
    // Check if there's an existing conversation
    const existingChat = existingConversations.find((chat) => chat.Profileid === Profileid && chat.chatID === generatedID);
  
    if (existingChat) {
      // Use the existing chat ID
      const existingChatID = existingChat.chatID;
      setNewUserChatID(existingChatID);
      console.log('Existing chat found. Chat ID:', existingChatID);
    } else {
      // Generate a new chat ID for the new user
      const generatedID = uuidv4();
      setNewUserChatID(generatedID);
  
      // Store this new conversation in your state
      setExistingConversations((prevConversations) => [
        ...prevConversations,
        { Profileid, chatID: generatedID },
      ]);
      console.log('Existing chat not found, generated new chat. Chat ID:', generatedID);
    }
  
    if (text.trim() !== '') {
      db.collection(collectionName).doc(generatedID).set(data)
        .then(() => {
          db.collection(collectionName).doc(generatedID).collection(subcollectionName).add(data)
            .then(() => {
              // Message sent successfully
            })
            .catch(error => {
              // Handle error
            });
        })
        .catch(error => {
          console.error("Error writing main document: ", error);
        });
      setText('');
    };
  }
  


const chatListItems = searchInput
  ? searchResults.map((item, index) => (
    <div className="chatItem" key={index}>
    <div className="imgbx">
      <img src={"http://localhost:5000/images/"+ item.id}  className="cover"  />
    </div>
    <div className="chatInfo">
      <p className="chatName" onClick={()=>chatblock_process(item.id,item.firstname)}>{item.firstname}</p>
    </div>
  </div>
  
    )): chatblock.map((item, index) => (
    <div className="chatItem" key={index}>
    <div className="imgbx">
      <img src={"http://localhost:5000/images/"+ item.id}  className="cover"  />
    </div>
    <div className="chatInfo">
      <p className="chatName" onClick={()=>chatblock_process(item.id,item.firstname)}>{item.firstname}</p>
      </div>
  </div>
    ));
const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      sendMessage();
    }
