import React, { useState, useEffect } from "react";
import { Link, useNavigate, } from "react-router-dom";
import { BrowserRouter, Routes, Route, Redirect } from "react-router-dom";
import "./login.css";
import axios from "axios";
import { FaRegUser } from "react-icons/fa";
import { BsFillEyeSlashFill } from "react-icons/bs"
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
const Login = () => {
  localStorage.setItem('update', 'new')
  localStorage.setItem('Edit', 'new')
  
  const [values, setValues] = React.useState({
    email: '',
    password: '',
  })
  const handleChange = (e) => {
    const { name, value } = e.target
    setValues({ ...values, [name]: value })
  }
  const navigate = useNavigate();
  function handleClick() {
    navigate("/form")
  }
  const Forgotpassword=()=>{
    navigate("/forgotPassword")
  }
  const history = useNavigate();
  const handleSubmit =async (e) => {
    e.preventDefault();
  const ProfileData=()=>{
    
  }
    axios.post('http://localhost:5000/pagelogin', { values })
      .then(res => {
        console.log("Status Login page",res.data.status);
        let Data=("ProfileSet_Monday",res.data.data);
      
    
      
        if (res.data.status == 200) {
          var Profilename = Data[0].firstname;
          console.log(Profilename);
          var Profileid = Data[0].id;
          var Prolastname = Data[0].lastname;
          var Proemail= Data[0].email;
          var Promobileno = Data[0].mobileno;
          var Procountry = Data[0].country;
          var Procity = Data[0].city;
          var Progender= Data[0].gender;
          var ProActive = Data[0].Active_Status;
          var Propassword = Data[0].password;
          var proprofile=Data[0].profile;
          var Access_Level=Data[0].AccessLevel;
          console.log(Profileid);
          localStorage.setItem("profileName",Profilename)
          localStorage.setItem("proid",Profileid)
          localStorage.setItem("Prolastname",Prolastname)
          localStorage.setItem("Proemail",Proemail)
          localStorage.setItem("login_email",email)
          localStorage.setItem("Promobileno",Promobileno)
          localStorage.setItem("Procountry",Procountry)
          localStorage.setItem("Procity",Procity)
          localStorage.setItem("Progender",Progender)
          localStorage.setItem("ProActive",ProActive)
          localStorage.setItem("Propassword",Propassword)
          localStorage.setItem("proprofile",proprofile)
          localStorage.setItem("Access_Level",Access_Level)
          localStorage.setItem('email', email)
          // alert('Successfully Login');
          history("/user/home", {state:Data});
    
        }
        else{
          alert("Incorrect email / password")
        }
       
      })

      setloggedOut(true)
  }
  React.useEffect(() => {
    if (localStorage.getItem('login_email')) history('/user')
  }, [])
  const { email, password } = values;

  const[loggedInstate,setloggedOut]=useState(false);
  return (
    <div className="login-page">
      
      <div className="left-section">
        {/* <img src="https://www.tigmatech.com/assets/img/logo.png" alt="Logo" className="logo" /> */}
        <h1 className="welcome-heading">Welcome TO <br/>Tigma Technology <br/> Leave System</h1>
      </div>
      
      <div className="right-section">
        <h1 className="login-text">Login to Tigma</h1>
        <form className="login-form">
       <div className="control-container">
       <input
              type="email"
              name="email"
              className="login-input"
              onChange={handleChange}
              placeholder="Email"
              required
            /> 

       </div>
       <div className="control-container">
       <input
              type="password"
              name="password"
              className="login-input"
              onChange={handleChange}
              placeholder="Password"
              required
            />
       </div>
          
          <button type="submit" className="login-button" onClick={handleSubmit}>Login</button>
          <div className="button-container"></div>
        
        </form>
        <div>
        <div >
          <a onClick={Forgotpassword} className="Signup-link">Forgot password</a>
      
        </div>{/* <a onClick={handleClick} className="Signup-link">Don't have an account? Sign up</a> */}
      
        </div>
      </div>
    </div>
  );
};

export default Login;
